import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const UserList = () => {
  let [alldata, updateAlldata] = useState([]);
  const [user, setUser] = useState({
    id: "",
    firstName: "",
    lastName: "",
    mobile: "",
    email: "",
    address: "",
    password: "",
    state: "",
    district: "",
    pincode: "",
  });

  const getuserinfo = () => {
    fetch("http://localhost:1250/details")
      .then((response) => response.json())
      .then((info) => {
        updateAlldata(info);
      });
  };
  useEffect(() => {
    getuserinfo();
    console.log(alldata);
  }, [1]);

  const show = (id) => {
    const url = `http://localhost:1250/details/${id}`;

    fetch(url)
      .then((response) => response.json())
      .then((serRes) => {
        console.log(serRes);
        setUser(serRes);
      });
  };
  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-lg-3 border">
          <h4 className="text-primary">Available Profile</h4>
          <div>
            {alldata.map((data, index) => {
              return (
                <ul className="list-group " key={index}>
                  <li className="text-primary" onClick={getuserinfo}>
                    <button className="btn" onClick={() => show(user.id)}>
                      {data.firstname}
                    </button>
                  </li>
                </ul>
              );
            })}
          </div>
        </div>
        <div className="col-lg-8 form bg-white shadow-lg rounded">
          <h4 className="text-center text-primary">Profile Detail</h4>
          <table cellPadding={30}>
            <thead>
              <tr>
                <th>Name : {user.firstName}</th>
                <th>Mobile : {user.mobile}</th>
              </tr>
              <tr>
                <th>Email : </th>
                <th>Password : </th>
              </tr>
              <tr>
                <th>Address : </th>
                <th>State : </th>
                <th>pincode : </th>
              </tr>
            </thead>
          </table>
        </div>
      </div>
    </div>
  );
};

export default UserList;

