import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const UserList = () => {
  let [alldata, updateAlldata] = useState([]);
  const [user, setUser] = useState({});

  const getuserinfo = () => {
    fetch("http://localhost:1250/details")
      .then((response) => response.json())
      .then((info) => {
        console.log(info);
        updateAlldata(info);
      });
  };
  useEffect(() => {
    getuserinfo();
  }, [1]);

  const specificUser = (id) => {
    const url = `http://localhost:1250/details/${id}`;

    fetch(url)
      .then((response) => response.json())
      .then((serRes) => {
        console.log(serRes);
        setUser(serRes);
      });
  };
  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-lg-3 ">
          <h4 className="text-primary">Available Profile</h4>
          <div className="form bg-white shadow-lg rounded">
            <ul className="list-group ">
              {alldata.map((data, index) => {
                return (
                  <Link
                    className="text-primary list-group-item"
                    key={index}
                    onClick={specificUser.bind(this, data.id)}
                  >
                    {data.firstname}
                  </Link>
                );
              })}
            </ul>
          </div>
        </div>
        <div className="col-lg-9 form bg-white shadow-lg rounded">
          <h4 className="text-center text-primary">Profile Detail</h4>
          <table>
            <tbody>
              {user.map((uinfo, index2) => {
                return (
                  <tr key={index2}>
                    <th>
                      Name : {uinfo.firstname} {uinfo.lastname}
                    </th>
                  </tr>
                );
              })}
            </tbody>
          </table>
          {/* <p>id: {user.id}</p>
          <p>First Name: {user.firstname}</p>
          <p>Last Name: {user.lastname}</p>
          <p>Email: {user.emailId}</p>
          <p>Mobile: {user.mobileNo}</p>
          <p>Address: {user.fulladdress}</p>
          <p>Pincode: {user.pincode}</p>
          <p>District: {user.district}</p>
          <p>State: {user.state}</p> */}
        </div>
        <Link to="/register">Go back</Link>
      </div>
    </div>
  );
};

export default UserList;

