import React, { useState, useEffect } from "react";

const Register = () => {
  let [fnameerror, updatefnameError] = useState("");
  let [fname, pickFname] = useState("");

  let [lnameerror, updatelnameError] = useState("");
  let [lname, pickLname] = useState("");

  let [mobileerror, updatemobileError] = useState("");
  let [mobile, pickMobile] = useState("");

  let [emailerror, updateemailError] = useState("");
  let [email, pickEmail] = useState("");

  let [passerror, updatepassError] = useState("");
  let [pass, pickPass] = useState("");

  let [cpasserror, updatecpassError] = useState("");
  let [cpass, pickCpass] = useState("");

  let [doberror, updatedobError] = useState("");
  let [dob, pickDob] = useState("");

  let [addresserror, updateaddressError] = useState("");
  let [address, pickAddress] = useState("");

  let [pinerror, updatepinError] = useState("");
  let [pin, pickPin] = useState("");

  let [stateerror, updatestateError] = useState("");
  let [state, pickState] = useState("");

  let [districterror, updatedistrictError] = useState("");
  let [district, pickDistrict] = useState("");

  //   const [isChecked, setIsChecked] = useState(false);

  let formstatus = true;

  const registration = () => {
    // firstname validation
    if (fname == "") {
      formstatus = false;
      updatefnameError("Invalid first Name");
    } else {
      updatefnameError("");
    }
    // lastname validation
    if (lname == "") {
      formstatus = false;
      updatelnameError("Invalid last Name");
    } else {
      updatelnameError("");
    }
    //mobile validation
    if (mobile == "") {
      formstatus = false;
      updatemobileError("Invalid mobile Number");
    } else {
      updatemobileError("");
    }
    //email validation
    if (email == "") {
      formstatus = false;
      updateemailError("Invalid email");
    } else {
      updateemailError("");
    }
    //password validation
    if (pass == "") {
      formstatus = false;
      updatepassError("Invalid password");
    } else {
      updatepassError("");
    }
    //confirm password validation
    if (cpass == "") {
      formstatus = false;
      updateErrocpassr("Invalid password");
    } else {
      updatecpassError("");
    }
    //dOB validation
    if (dob == "") {
      formstatus = false;
      updatedobError("Invalid Date of Birth");
    } else {
      updatedobError("");
    }
    //address validation
    if (address == "") {
      formstatus = false;
      updateaddressError("Invalid email");
    } else {
      updateaddressError("");
    }
    //pincode validation
    if (pin == "") {
      formstatus = false;
      updatepinError("Invalid email");
    } else {
      updatepinError("");
    }
    //state validation
    if (state == "") {
      formstatus = false;
      updatestateError("Invalid email");
    } else {
      updatestateError("");
    }
    //district validation
    if (email == "") {
      formstatus = false;
      updatedistrictError("Invalid email");
    } else {
      updatedistrictError("");
    }

    var url = "http://localhost:1250/details";
    var data = {
      firstname: fname,
      lastname: lname,
      mobileNo: mobile,
      emailId: email,
      password: pass,
      confirmpassword: cpass,
      fulladdress: address,
      pincode: pin,
      state: state,
      district: district,
    };
    var postOption = {
      headers: { "Content-Type": "application/json" },
      method: "POST",
      body: JSON.stringify(data),
    };
    fetch(url, postOption)
      .then((response) => response.json())
      .then((serRes) => {
        pickFname("");
        pickLname("");
        alert("savingh in json");
      });
  };

  //   const handleChange = (event) => {
  //     setIsChecked(event.target.checked);

  //     // 👇️ this is the checkbox itself
  //     console.log(event.target);

  //     // 👇️ this is the checked value of the field
  //     console.log(event.target.checked);
  //   };
  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-lg-6">
          <h4>REGISTER AS CANDIDATE</h4>
          <div class="form-group row">
            <label class="col-sm-4 col-form-label">First Name :</label>
            <div class="col-sm-6">
              <input
                type="text"
                class="form-control"
                onChange={(e) => pickFname(e.target.value)}
                value={fname}
              />
            </div>
          </div>
          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">Last Name :</label>
            <div class="col-sm-6">
              <input
                type="text"
                class="form-control"
                onChange={(e) => pickLname(e.target.value)}
                value={lname}
              />
            </div>
          </div>
          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">Mobile No :</label>
            <div class="col-sm-6">
              <input
                type="text"
                class="form-control"
                onChange={(e) => pickMobile(e.target.value)}
                value={mobile}
              />
            </div>
          </div>
          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">Email Id :</label>
            <div class="col-sm-6">
              <input
                type="text"
                class="form-control"
                onChange={(e) => pickEmail(e.target.value)}
                value={email}
              />
            </div>
          </div>
          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">Password :</label>
            <div class="col-sm-6">
              <input
                type="text"
                class="form-control"
                placeholder="Password max 8 character"
                onChange={(e) => pickPass(e.target.value)}
                value={pass}
              />
            </div>
          </div>
          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">Confirm Password :</label>
            <div class="col-sm-6">
              <input
                type="text"
                class="form-control"
                placeholder=" Confirm password "
                onChange={(e) => pickCpass(e.target.value)}
                value={cpass}
              />
            </div>
          </div>

          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">Date Of Birth :</label>
            <div class="col-sm-6">
              <input
                type="date"
                class="form-control"
                onChange={(e) => pickDob(e.target.value)}
                value={dob}
              />
            </div>
          </div>

          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">Full Address :</label>
            <div class="col-sm-6">
              <textarea
                class="form-control"
                onChange={(e) => pickAddress(e.target.value)}
                value={address}
              />
            </div>
          </div>
          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">Pincode : </label>
            <div class="col-sm-6">
              <input
                type="text"
                class="form-control"
                onChange={(e) => pickPin(e.target.value)}
                value={pin}
              />
            </div>
          </div>
          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">State : </label>
            <div class="col-sm-6">
              <select
                type="text"
                class="form-control"
                onChange={(e) => pickState(e.target.value)}
                value={state}
              >
                <option></option>
                <option>Karnataka </option>
                <option>Delhi</option>
                <option>Bihar</option>
                <option>Himachal</option>
                <option>Punjab</option>
                <option>Rajasthan</option>
                <option>Maharashtra</option>
              </select>
            </div>
          </div>
          <div class="form-group row mt-2">
            <label class="col-sm-4 col-form-label">District : </label>
            <div class="col-sm-6">
              <select
                type="text"
                class="form-control"
                onChange={(e) => pickDistrict(e.target.value)}
                value={district}
              >
                <option></option>
                <option>Bangalore</option>
                <option>New Delhi</option>
                <option>Patna</option>
                <option>Kangra</option>
                <option>Chandigarh</option>
                <option>Jaipur</option>
                <option>Pune</option>
              </select>
            </div>
          </div>
          <div class="form-check mt-4 text-primary ">
            <input
              className="form-check-input"
              type="checkbox"
              value=""
              id="flexCheckDefault"
              onChange={handleChange}
              checked={isChecked}
            />
            <label class="form-check-label" for="flexCheckDefault">
              Agree on Terms Conditions
            </label>
          </div>
          <div className="text-center">
            <button className="btn btn-primary">Register</button>

            <button className="btn btn-primary">Clear</button>
          </div>
        </div>

        <div className="col-lg-6">
          <h2>Your Details</h2>
          <div>
            <h6 className="mb-3 mt-4">
              <b>First Name :</b> {fname}
            </h6>
            <h6 className="mb-3 mt-4">Last Name : {lname}</h6>
            <h6 className="mb-3 mt-4">Mobile No : {mobile}</h6>
            <h6 className="mb-3 mt-4">e-Mail : {email}</h6>
            <h6 className="mb-3 mt-4">Password : {pass}</h6>
            <h6 className="mb-3 mt-4">Confirm Password : {cpass}</h6>
            <h6 className="mb-3 mt-4">Date Of Birth : {dob}</h6>
            <h6 className="mb-3 mt-4">Address : {address}</h6>
            <h6 className="mb-3 mt-4">Pincode : {pin}</h6>
            <h6 className="mb-3 mt-4">State : {state}</h6>
            <h6 className="mb-3 mt-4">District : {district}</h6>
            <h6 className="mb-3 mt-4">T&C : {isChecked}</h6>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Register;
