import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Pdf from "react-to-pdf";

const ref = React.createRef();

const UserList2 = () => {
  let [alldata, updateAlldata] = useState([]);
  const [user, setUser] = useState({});

  const getuserinfo = () => {
    fetch("http://localhost:1250/details")
      .then((response) => response.json())
      .then((info) => {
        console.log(info);
        updateAlldata(info);
      });
  };
  useEffect(() => {
    getuserinfo();
  }, [1]);

  const specificUser = (id) => {
    const url = `http://localhost:1250/details/${id}`;

    fetch(url)
      .then((response) => response.json())
      .then((serRes) => {
        console.log(serRes);
        setUser(serRes);
      });
  };
  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-lg-3 fw-semibold">
          <h4 className="text-primary text-center">Available Profile</h4>
          <div className="form bg-white shadow-lg rounded">
            <ul className="list-group ">
              {alldata.map((data, index) => {
                return (
                  <Link
                    className="text-primary list-group-item"
                    key={index}
                    onClick={specificUser.bind(this, data.id)}
                  >
                    {data.firstname}
                  </Link>
                );
              })}
            </ul>
          </div>
        </div>

        <div className="col-lg-9 form bg-white shadow-lg rounded ">
          <div className="text-end mb-2">
            <Pdf targetRef={ref} filename="userprofile.pdf">
              {({ toPdf }) => (
                <button className="btn btn-info me-2" onClick={toPdf}>
                  Generate Pdf
                </button>
              )}
            </Pdf>

            <button className="btn btn-success">Excel</button>
          </div>
          <div ref={ref} className="mt-5">
            <h4 className="text-center text-primary mt-3">Profile Detail</h4>

            <div className="row mt-3">
              <table>
                <tbody>
                  <tr>
                    <th>First Name : </th>
                    <td>{user.firstname}</td>
                    <th>Last Name : </th>
                    <td>{user.lastname}</td>
                  </tr>
                  <tr>
                    <th>Email : </th>
                    <td>{user.emailId}</td>
                    <th>Mobile : </th>
                    <td>{user.mobileNo}</td>
                  </tr>
                  <tr>
                    <th>Address : </th>
                    <td>{user.fulladdress}</td>
                    <th>Pincode :</th>
                    <td>{user.pincode}</td>
                  </tr>
                  <tr>
                    <th>District : </th>
                    <td>{user.district}</td>
                    <th>State : </th>
                    <td>{user.state}</td>
                  </tr>
                </tbody>
              </table>
            </div>
            {/* <div className="text-center mb-2">
                <button className="btn btn-warning">Next</button>
              </div> */}
          </div>
        </div>
      </div>
      <Link to="/register">Go back</Link>
    </div>
  );
};

export default UserList2;

// npm install react-html-to-excel
