import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const UserList = () => {
  let [alldata, updateAlldata] = useState([]);
  const [user, setUser] = useState({});

  const getuserinfo = () => {
    fetch("http://localhost:1250/details")
      .then((response) => response.json())
      .then((info) => {
        console.log(info);
        updateAlldata(info);
      });
  };
  useEffect(() => {
    getuserinfo();
  }, [1]);

  const specificUser = (id) => {
    const url = `http://localhost:1250/details/${id}`;

    fetch(url)
      .then((response) => response.json())
      .then((serRes) => {
        console.log(serRes);
        setUser(serRes);
      });
  };
  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-lg-3 fw-semibold">
          <h4 className="text-primary text-center">Available Profile</h4>
          <div className="form bg-white shadow-lg rounded">
            <ul className="list-group ">
              {alldata.map((data, index) => {
                return (
                  <Link
                    className="text-primary list-group-item"
                    key={index}
                    onClick={specificUser.bind(this, data.id)}
                  >
                    {data.firstname}
                  </Link>
                );
              })}
            </ul>
          </div>
        </div>
        <div className="col-lg-9 form bg-white shadow-lg rounded ">
          <h4 className="text-center text-primary mt-3">Profile Detail</h4>
          <div className="row mt-3">
            <div className="col-lg-3 me-5 p-5">
              <p>
                <b>id : </b>
                {user.id}
              </p>
              <p>First Name : {user.firstname}</p>
              <p>
                <b>Last Name :</b> {user.lastname}
              </p>
            </div>
            <div className="col-lg-3 me-5 p-5">
              <p>
                <b>Email :</b> {user.emailId}
              </p>
              <p>
                <b>Mobile :</b> {user.mobileNo}
              </p>
              <p>
                <b>Address :</b> {user.fulladdress}
              </p>
            </div>
            <div className="col-lg-3 me-5 p-5">
              <p>
                <b>Pincode : </b>
                {user.pincode}
              </p>
              <p>
                <b>District :</b> {user.district}
              </p>
              <p>
                <b>State :</b> {user.state}
              </p>
            </div>
            <div className="text-center mb-2">
              <button className="btn btn-warning">Next</button>
            </div>
          </div>
        </div>
        <Link to="/register">Go back</Link>
      </div>
    </div>
  );
};

export default UserList;
