import React, { useState } from "react";
import { Link } from "react-router-dom";

const MyPersonal = () => {
  let [mobileerror, updateMobileError] = useState("");
  let [mobile, updateMobile] = useState("");

  let [emailerror, updateEmailError] = useState("");
  let [email, updateEmail] = useState("");

  let [passworderror, updatePasswordError] = useState("");
  let [password, updatePassword] = useState("");

  let [statusmsg, updateStatus] = useState("");

  let [nameerror, updateNameError] = useState("");
  let [name, pickName] = useState("");

  let [gendererror, updateGenderError] = useState("");
  let [gender, updateGender] = useState("");

  let [ageerror, updateAgeError] = useState("");
  let [age, updateAge] = useState("");

  const saveData = () => {
    // toast("Invalid inputs!");

    // name validation
    if (name == "") {
      formstatus = false;
      updateNameError("Invalid Bill Name");
    } else {
      updateNameError("");
    }
    // Age validation
    if (age == "") {
      formstatus = false;
      updateAgeError("Invalid Amount");
    } else {
      updateAgeError("");
    }
    // City validation
    if (bstatus == "") {
      formstatus = false;
      updateStatusError("Invalid Status");
    } else {
      updateStatusError("");
    }
    if (mobile == "") {
      formstatus = false;
      updateMobileError("Invalid Amount");
    } else {
      updateMobileError("");
    }
    if (password == "") {
      formstatus = false;
      updatePasswordError("Invalid Amount");
    } else {
      updatePasswordError("");
    }
    if (email == "") {
      formstatus = false;
      updateEmailError("Invalid Amount");
    } else {
      updateEmailError("");
    }

    if (formstatus == true) {
      var url = "http://localhost:1240/personal";
      var billInfo = {
        name: name,
        amount: bamount,
        status: bstatus,
      };
      var postoption = {
        headers: { "Content-Type": "application/json" },
        method: "POST",
        body: JSON.stringify(billInfo),
      };
      fetch(url, postoption)
        .then((response) => response.json())
        .then((servRes) => {
          pickName("");
          pickAmount("");
          pickStatus("");
          swal("Are you sure you want to do this?", {
            buttons: ["No!", "Yes!"],
          }).then(function () {
            window.location.href = "#/billing";
          });
        });
    } else {
      updateStatus("Invalid Inputs");
    }
  };

  return (
    <div className="container mt-4 p-4 rounded">
      <div className="row mt-5">
        <div className="col-lg-12">
          <h2 className="text-center text-info">Personal Details</h2>
        </div>
        <div class="col-lg-3">
          <div className=" text-primary">
            <ul class="list-group">
              <li class="list-group-item active ">Profile Setting</li>
              <Link to="/personal" class="list-group-item text-primary">
                <i className="fa fa-user"></i> Personal Details
              </Link>
              <Link to="/contact" class="list-group-item text-primary">
                <i className="fa fa-headset"></i> Contact Details
              </Link>
              <Link to="/education" class="list-group-item text-primary">
                <i className="fa fa-book"></i> Education Details
              </Link>
              <Link to="/skill" class="list-group-item text-primary">
                <i className="fa fa-desktop"></i>
                My Skills & Expeerience
              </Link>
              <Link to="/resume" class="list-group-item text-primary">
                <i className="fa fa-file"></i>My Resume
              </Link>
            </ul>
          </div>
        </div>
        <div className="col-lg-6">
          <div className="p-4 bg-white">
            <div className="mb-3">
              <label>Full Name</label>
              <input
                type="text"
                className="form-control"
                onChange={(e) => updateName(e.target.value)}
                value={gender}
              ></input>
            </div>
            <div className="row">
              <div className="col-lg-6 mb-3">
                <label>Gender</label>
                <select
                  className="form-control"
                  onChange={(e) => updateGender(e.target.value)}
                  value={name}
                >
                  <option>Male</option>
                  <option>Female</option>
                </select>
              </div>
              <div className="col-lg-6 mb-3">
                <label>Age</label>
                <input
                  type="text"
                  className="form-control"
                  onChange={(e) => updateAge(e.target.value)}
                  value={age}
                ></input>
              </div>
              <div className="mb-3">
                <label>Mobile No</label>
                <input
                  type="number"
                  className="form-control"
                  onChange={(e) => updateMobile(e.target.value)}
                  value={mobile}
                ></input>
              </div>
            </div>
            <div className="row">
              <div className="col-lg-6">
                <label>e-Mail Id</label>
                <input
                  type="email"
                  className="form-control"
                  onChange={(e) => updateEmail(e.target.value)}
                  value={email}
                ></input>
              </div>
              <div className="col-lg-6">
                <label>Password</label>
                <input
                  type="text"
                  className="form-control"
                  onChange={(e) => updatePassword(e.target.value)}
                  value={password}
                ></input>
              </div>
            </div>
          </div>
        </div>
        <div className="col-lg-12 text-center">
          <div className="mt-4 text-center">
            <button className="btn btn-primary " onClick={saveData}>
              Update Profile
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MyPersonal;
